import React, { useState } from 'react';
import '../App.css';
import './HeroSection.css';
import CardItem from './CardItem';
import Select from 'react-select';

const defaultValue = 'default';

const user_options = [
  {label:'Any', value: defaultValue}, {label:'IT Admins', value: 'ITadmins'},
  {label:'Parents', value: 'parents'}, {label:'Principals', value: 'principals'},
  {label:'Students', value: 'students'}, {label:'Teachers', value: 'teachers'}
];
const content_options = [
  {label: 'Any', value: defaultValue}, {label: 'Best practices', value: 'best-practices'},
  {label: 'Checklist', value: 'checklist'}, {label: 'Framework', value: 'framework'},
  {label: 'Guide', value: 'guide'},{label: 'Infographic', value: 'infographic'},
  {label: 'Lesson', value: 'lesson'}, {label: 'Letter', value: 'letter'},
  {label: 'Presentation', value: 'presentation'}, {label: 'Principles', value: 'principles'},
  {label: 'Report', value: 'report'}, {label: 'Resources', value: 'resources'},
  {label: 'Survey', value: 'survey'}, {label: 'Template', value: 'template'},
  {label: 'Toolkit', value: 'toolkit'}, {label: 'Webinar', value: 'webinar'},
  {label: 'Website', value: 'website'}
];
const type_options = [
  {label: 'Any', value: defaultValue}, {label: 'Document', value: 'document'}, {label: 'Image', value: 'image'},
  {label: 'Video', value: 'video'}, {label: 'Website', value: 'website'}
];
const language_options = [
  {label: 'Any', value: defaultValue}, {label: 'English', value: 'en'}, {label: 'Spanish', value: 'es'}
];

const selectStyle = {
  control: styles => (
    { 
      ...styles,
      width: '100%',
      borderRadius: '15px',
    }
  ),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    return {
      ...styles,
      color: 'rgb(100,100,100)',
    };
  },
};

var selected_user = defaultValue;
var selected_content = defaultValue;
var selected_type = defaultValue;
var selected_language = defaultValue;


function handleUserChange(e) { selected_user = e.value; }

function handleContentChange(e) { selected_content = e.value; }

function handleTypeChange(e) { selected_type = e.value; }

function handleLanguageChange(e) { selected_language = e.value; }







function HeroSection() {

  const [resources, setResources] = useState([]);
  var didWeSearch = false;

  async function search() {

    didWeSearch = true;
    var url = 'http://localhost:8081/resources'
    + '?user=' + selected_user + '&content=' + selected_content
    + '&type=' + selected_type + '&language=' + selected_language;
  
    const res = await fetch(url);
    setResources(await res.json());
    
    // MANAGE THE CASE THAT WE GET AN EMPTY RESULT !!!!!
    console.log(resources);
    console.log(resources[0]);
  }



  if (!didWeSearch) {
    return (
      <div className='hero-container'>
        <h1>Search for resources</h1>

        <div className='hero-subtitle'>
          <h2>Good practices for data fragility are essential in academics</h2>
          <h2>You will find all the resources you need here</h2>
        </div>


        <div className='hero-search-bar'>

          <div className='hero-search-item'>
            <p className='hero-search-label'>User</p>
            <Select className='hero-search-select' options={user_options} defaultValue={user_options[0]} onChange={handleUserChange} styles={selectStyle}/>
          </div>

          <div className='hero-search-item'>
            <p className='hero-search-label'>Content</p>
            <Select className='hero-search-select' options={content_options} defaultValue={content_options[0]} onChange={handleContentChange} styles={selectStyle}/>
          </div>

          <div className='hero-search-item'>
            <p className='hero-search-label'>Type</p>
            <Select className='hero-search-select' options={type_options} defaultValue={type_options[0]} onChange={handleTypeChange} styles={selectStyle}/>
          </div>

          <div className='hero-search-item'>
            <p className='hero-search-label'>Language</p>
            <Select className='hero-search-select' options={language_options} defaultValue={language_options[0]} onChange={handleLanguageChange} styles={selectStyle}/>
          </div>


          <button onClick={search} className='hero-search-button'></button>

        </div>


        <div className='hero-element-container'>
          <CardItem title='TITULO {resources[0].title}' author='' description='DESCRIPTION HOLA SOY MC REVOLVER Y SOY RAPERO, mi madre ama de casa mi padre panadero yo esto es una prueba de una descripcion larga y me daba palo copiar un lorem ipsum asi que escribi esto {resources[0].description}' link='LINK {resources[0].link}'  />
          <CardItem title="TITULO {resources[0].title}" author="AUTOR HOSTIA ESTE DERREPENTE ES TO LARGO NO O QUE {resources[0].author}" description="DESCRIPTION {resources[0].description}" link="LINK {resources[0].link}"  />
          <CardItem title="TITULO BIEN LARGO PAPA MENUDA LOCURAAAA{resources[0].title}" author="FEDERICO GARCIA LORCA Y LA ASOCIACION DE ESCULTORES DE PECES {resources[0].author}" description="Similique at eos quaerat est quae repellendus. Maxime voluptas sed sunt voluptates sunt unde saepe. Debitis sint adipisci maiores voluptatem facere et quod. Suscipit recusandae rerum odit voluptas cum est. Unde dolorem rerum cupiditate nobis quia. Id nihil amet maxime consectetur molestiae eum est. Sequi beatae culpa ut vitae similique cupiditate laborum quo. Quo ut vero tempore. Dolore rerum ipsam quis inventore voluptates. Temporibus velit et provident dolores. Tempore repudiandae reiciendis nihil dicta inventore ut qui. Ex amet nesciunt et dolorem at. Facere placeat quis est est voluptate est dolorem rerum. Aut autem quidem commodi esse qui. Repudiandae molestias consectetur sit facere incidunt et quae. Eos aliquid vero aut qui. Quas sint fugiat qui. Eligendi repellendus asperiores dolorum et necessitatibus illo quo. Quae veniam assumenda officiis odit vel voluptatem fugit reprehenderit. Iure eius at aspernatur. Id voluptas quod officiis cumque atque soluta. Sequi dolor possimus id. Dolores voluptate fuga quia doloremque mollitia quasi non dignissimos. Pariatur dolore similique saepe eveniet. Accusamus est laborum velit aliquam aut aut nam id." link="LINK {resources[0].link}"  />
        </div>

      </div>
    );
  } else if (didWeSearch) {
    if (!resources) {
      <div className='hero-container'>
      <h1>Search for resources</h1>

      <div className='hero-subtitle'>
        <h2>Good practices for data fragility are essential in academics</h2>
        <h2>You will find all the resources you need here</h2>
      </div>


      <div className='hero-search-bar'>

        <div className='hero-search-item'>
          <p className='hero-search-label'>User</p>
          <Select className='hero-search-select' options={user_options} defaultValue={user_options[0]} onChange={handleUserChange} styles={selectStyle}/>
        </div>

        <div className='hero-search-item'>
          <p className='hero-search-label'>Content</p>
          <Select className='hero-search-select' options={content_options} defaultValue={content_options[0]} onChange={handleContentChange} styles={selectStyle}/>
        </div>

        <div className='hero-search-item'>
          <p className='hero-search-label'>Type</p>
          <Select className='hero-search-select' options={type_options} defaultValue={type_options[0]} onChange={handleTypeChange} styles={selectStyle}/>
        </div>

        <div className='hero-search-item'>
          <p className='hero-search-label'>Language</p>
          <Select className='hero-search-select' options={language_options} defaultValue={language_options[0]} onChange={handleLanguageChange} styles={selectStyle}/>
        </div>


        <button onClick={search} className='hero-search-button'></button>

      </div>


      <div className='hero-element-container'>
        <p>Loading...</p>
      </div>

    </div>
    }
  }
}

export default HeroSection;
