import React from 'react';
import '../App.css';
import './HeroSection.css';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

const user_options = [
  'Any', 'IT Admins', 'Parents', 'Principals', 'Students', 'Teachers'
];
const content_options = [
  'Any', 'Best practices', 'Checklist', 'Framework', 'Guide', 'Infographic',
  'Lesson', 'Letter', 'Presentation', 'Principles', 'Report', 'Resources',
  'Survey', 'Template', 'Toolkit', 'Webinar', 'Website'
];
const type_options = [
  'Any', 'Document', 'Image', 'Video', 'Website'
];
const language_options = [
  'Any', 'English', 'Spanish'
];


const colourStyles = {
  control: styles => ({backgroundColor: 'white'}),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    const color = '#3A6AAD';
    return {
      styles,
      backgroundColor: isDisabled ? 'red' : 'blue',
      color: '#FFF',
      cursor: isDisabled ? 'not-allowed' : 'default',
    };
  },
};





function search() {
  alert("Let's search!");
}

function HeroSection() {
  return (
    <div className='hero-container'>
      <h1>Search for resources</h1>

      <div className='hero-subtitle'>
        <h2>Good practices for data fragility are essential in academics</h2>
        <h2>You will find all the resources you need here</h2>
      </div>


      <div className='hero-search-bar'>

        <div className='hero-search-item'>
          <p className='hero-search-label'>User</p>
          <Dropdown className='hero-search-dropdown' options={user_options} placeholder="Any"/>
        </div>

        <div className='hero-search-item'>
          <p className='hero-search-label'>Content</p>
          <Dropdown className='hero-search-dropdown' options={content_options} placeholder="Any"/>
        </div>

        <div className='hero-search-item'>
          <p className='hero-search-label'>Type</p>
          <Dropdown className='hero-search-dropdown' options={type_options} placeholder="Any"/>
        </div>

        <div className='hero-search-item'>
          <p className='hero-search-label'>Language</p>
          <Dropdown className='hero-search-dropdown' options={language_options} placeholder="Any"/>
        </div>

        <button onClick={search} className='hero-search-button'></button>

      </div>


      <div className='hero-element-container'>

      </div>

    </div>
  );
}

export default HeroSection;
